import React, { useState, useEffect } from 'react';
import './ProgressBar.css';

class ProgressBar extends React.Component {
    constructor(props) {
      super(props)

      this.state = {
        percentage: 0
      }

      this.nextStep = this.nextStep.bind(this)
    }

    nextStep() {
      if(this.state.percentage === 100) return 
      this.setState(prevState => ({ percentage: prevState.percentage + (1/15) }))
    }

    render() {
      return (
        <div>

          <h2> A React Progress Bar </h2>
          <Progress percentage={this.state.percentage} />

          <div style={{ marginTop: '20px' }}>  
            <button 
              onClick={this.nextStep}
             >
              Next Step
            </button>  
          </div>   

          <div style={{marginTop: '10px', color: 'blue', marginBottom: '15px'}} onClick={() => this.setState({ percentage: 0 })}>
            Reset
          </div>
        </div>
      )
    }  
  }

  const Progress = (props) => {
    return (
        <div className="progress-bar">
          <Filler percentage={props.percentage} />
        </div>
      )
  }

  const Filler = (props) => {
    return <div className="filler" style={{ width: `${props.percentage}%` }} />
  }

//   ReactDOM.render(
//     <ProgressBarExample />,
//     document.querySelector('#app')
//   )

export default ProgressBar;
