import React, { useState, useEffect } from 'react';
import './ProgressBar.css';

const ProgressBar = () => {

    const [percentage, setPercentage] = useState(0)

    useEffect(() => {
        console.log(percentage)
        if(percentage === 100 || percentage > 100) return  
        setTimeout(() => setPercentage(percentage + (100/15)), 500);
    }, [percentage]);

    return (
        <div className="MainDiv">  
            <h1 className="MainTitle" > Progress Bar </h1>
            <Progress percentage={percentage} />

            <div style={{ marginTop: '20px' }}>  
            {/* <button 
                onClick={this.nextStep}
                >
                Next Step
            </button>   */}
            </div>   

            <div style={{marginTop: '10px', color: 'blue', marginBottom: '15px'}} onClick={() => this.setState({ percentage: 0 })}>
            Reset
            </div>
        </div>
    ) 
}

const Progress = (props) => {
    return (
        <div className="progress-bar">
          <Filler percentage={props.percentage} />
        </div>
      )
  }

  const Filler = (props) => {
    return <div className="filler" style={{ width: `${props.percentage}%` }} />
  }


export default ProgressBar;
