import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import './ProgressBar.css';



const ProgressBar = () => {

    const [percentage, setPercentage] = useState(0)
    const [start, setStart] = useState(false)
    const [reset, setReset] = useState(false)

    useEffect(() => {
        if(start) {
            if(percentage < 100) {  
                setTimeout(() => setPercentage(percentage + (100/15)), 500);
            }
        }
        if(reset) {
            setPercentage(0)
            setStart(false)
        }
    }, [percentage,start,reset]);


    return (
        <div className="MainDiv">  
            <h1 className="MainTitle" > Progress Bar </h1>
            <Progress percentage={percentage} />

            <div style={{ marginTop: '20px' }}>  
           <Button
                onClick={()=>{setStart(true)
                    setReset(false)
                }}
           >
               Start
           </Button>
           <Button
                onClick={()=>{
                    setStart(false)
                }}
           >
               Stop
           </Button>
           <Button
                onClick={()=>setReset(true)}
           >
               Reset
           </Button>
            </div>   
        </div>
    ) 
}

const Progress = (props) => {

    const defaultProps = {
        bgcolor: 'background.paper',
        m: 1,
        border: 1,
        style: { width: '1rem', height: '1rem' },
      };
    return (
        <>
        <Box display="flex" justifyContent="center">
            <Box borderColor="text.primary" {...defaultProps} 	bgcolor="#81c784" />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
            <Box borderColor="text.primary" {...defaultProps} />
        </Box>          
        <div className="progress-bar">
            <Filler percentage={props.percentage} />
        </div>
        </>
      )
  }

  const Filler = (props) => {
    return <div className="filler" style={{ width: `${props.percentage}%` }} />
  }


export default ProgressBar;
